import { useDispatch, useSelector } from 'react-redux';
import {
  getAlertedMachines,
  getMachineConfig,
  getMachines,
} from 'store/machine/machine.selectors';
import Table from 'components/Table';
import { useEffect, useState } from 'react';
import Modal from './Modal';
import styled from 'styled-components';
import {
  updateMachineConfig,
  updateAlertedMachines,
  removeAlertedMachines,
} from 'store/machine/machine.actions';
import MachineBlock from './MachineBlock';
import { ALERT } from 'constants/enum';

const Tile = () => {
  const [showAlert, setShowAlert] = useState<boolean>(false);
  const [pageNumber, setPageNumber] = useState<number>(1);

  const alertedMachines = useSelector(getAlertedMachines);
  const machineConfig = useSelector(getMachineConfig);
  const machines = useSelector(getMachines);

  const dispatch = useDispatch();

  const setAlert = (mc) => {
    const idleHours = mc.cumulativeIdleHours.hour;
    const operatingHours = mc.cumulativeOperatingHours.hour;
    if (operatingHours / idleHours < 3) {
      mc.alert = ALERT.IDLE;
      return mc;
    }
    const fuelRemaining = mc.fuelRemaining.percent;

    if (fuelRemaining < 5) {
      mc.alert = ALERT.FUEL_LIMIT;
      return mc;
    }

    if (idleHours < 5 || operatingHours < 5) {
      mc.alert = ALERT.WEEKEND_OFF;
      return mc;
    }
    const engineStatus = mc.engineStatus.running;

    if (!engineStatus) {
      mc.alert = ALERT.ENGINE_FAILED;
      return mc;
    }
    return mc;
  };

  useEffect(() => {
    console.log(machineConfig, showAlert, alertedMachines);
    if (!showAlert && alertedMachines.length) {
      console.log(alertedMachine);
      dispatch(updateAlertedMachines(alertedMachines));
      setShowAlert(true);
    }
  }, [machineConfig, showAlert, alertedMachines]);

  return (
    <Styled.Dashboard>
      {showAlert && alertedMachines?.length && (
        <Modal
          onClose={() => {
            setShowAlert(false);
            dispatch(removeAlertedMachines());
          }}
        >
          <Styled.Container>
            {alertedMachines.map((aM) => (
              <div key={aM.id}>
                <MachineBlock alertedMachine={aM} />
              </div>
            ))}
          </Styled.Container>
        </Modal>
      )}
      <Table data={machines} setPageNumber={setPageNumber} />
    </Styled.Dashboard>
  );
};

const Styled = {
  Dashboard: styled.div`
    padding: ${(props) => props.theme.spacings.xl};
  `,
  Container: styled.div`
    background-color: ${(props) => props.theme.colors.white};
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
  `,
};

export default Tile;
