import { useSelector } from 'react-redux';
import { getMachineConfig } from 'store/machine/machine.selectors';
import Table from 'components/Table';
import { useEffect, useState } from 'react';
import Modal from './Modal';
import styled from 'styled-components';
import { MachineConfig } from 'store/machine/machine.types';

const Tile = () => {
  const [showAlert, setShowAlert] = useState<boolean>(false);
  const [pageNumber, setPageNumber] = useState<number>(1);
  const [alertedMachines, setAlertedMachines] = useState<MachineConfig[]>();
  const machineConfig = useSelector(getMachineConfig);

  useEffect(() => {
    if (machineConfig) {
      const alertedMachines = machineConfig.filter((mc) => {
        const operatingHours = mc.cumulativeIdleHours.hour;
        const idleHours = mc.cumulativeOperatingHours.hour;
        if (operatingHours / idleHours < 3) {
          return mc;
        }
        return false;
      });
      if (alertedMachines.length) {
        setAlertedMachines(alertedMachines);
        setShowAlert(true);
      }
    }
  }, [machineConfig]);

  return (
    <>
      {showAlert && (
        <Modal onClose={() => setShowAlert(false)}>
          <Styled.Container></Styled.Container>
        </Modal>
      )}
      <Table data={machineConfig} setPageNumber={setPageNumber} />
    </>
  );
};

const Styled = {
  Container: styled.div`
    background-color: ${(props) => props.theme.colors.white};
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
  `,
};

export default Tile;
