import { createSelector } from 'reselect';
import { State } from './projects.types';

export const projectsState = (state: { projectsApp: State }) => {
  return state && state.projectsApp;
};

export const getProjects = createSelector(
  projectsState,
  (projects) => projects.projects
);
export const getLoadingState = createSelector(
  projectsState,
  (projects) => projects.loading
);
export const getShowDrawer = createSelector(
  projectsState,
  (projects) => projects.showDrawer
);
export const getShowSlideOutAnim = createSelector(
  projectsState,
  (projects) => projects.showSlideOutAnim
);
export const getPageNumber = createSelector(
  projectsState,
  (projects) => projects.pageNumber
);
export const getCurrentProject = createSelector(
  projectsState,
  (projects) => projects.currentProject
);
export const getProjectLoadingState = createSelector(
  projectsState,
  (projects) => projects.projectLoading
);
