import React, { createContext } from 'react';
import io from 'socket.io-client';
import { useDispatch } from 'react-redux';
import { updateMachineConfig } from './machine.actions';
import { MachineConfig } from './machine.types';

export const WebSocketProvider = ({ children }: any) => {
  const WebSocketContext = createContext<any>(null);
  let socket: any = null;
  let ws;

  const dispatch = useDispatch();

  if (!socket) {
    //@ts-ignore
    socket = io('http://localhost:5000');

    socket.io.on('machineUpdater', (data: MachineConfig[]) => {
      const payload = data.map((c) => ({
        id: c.id,
        name: c.equipmentHeader.OEMName,
        fuelUsed: c.fuelUsed.fuelConsumed,
      }));
      console.log('id', payload, '\n');
      dispatch(updateMachineConfig(payload));
    });

    ws = {
      socket: socket,
    };
  }
  return (
    <WebSocketContext.Provider value={ws}>{children}</WebSocketContext.Provider>
  );
};
