import Pagination from 'components/Pagination';
import React from 'react';
import { useTable } from 'react-table';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import { MachineConfigX } from 'store/machine/machine.types';

interface Props {
  data: any;
  setPageNumber: (data: number) => void;
  pageNumber: number;
  setLimit: (data: number) => void;
  limit: number;
  total?: number;
  pages?: number;
}

export default function Table({
  data,
  setPageNumber,
  setLimit,
  limit,
  pageNumber,
  total,
  pages,
}: Props) {
  const dispatch = useDispatch();

  const columns = React.useMemo(
    () => [
      {
        id: 'MachineTable',
        Header: () => undefined,
        hideHeader: false,
        columns: [
          {
            Header: 'Name',
            accessor: 'name',
          },
          {
            Header: 'Fuel Used',
            accessor: 'fuelUsed',
          },
        ],
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, prepareRow, rows } =
    useTable({
      columns,
      data: mapDataForTable(data || []),
    });

  return (
    <>
      <Styled.Table {...getTableProps}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column: any) => {
                return column.hideHeader === false ? null : (
                  <th {...column.getHeaderProps()}>
                    {column.render('Header')}
                  </th>
                );
              })}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr onClick={() => {}} {...row.getRowProps()}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </Styled.Table>
      <Styled.Divider />
      <Pagination
        pageCount={pages ?? 3}
        pageRangeDisplayed={3}
        marginPagesDisplayed={2}
        forcePage={pageNumber - 1}
        onPageChange={(data) => setPageNumber(data.selected + 1)}
      />
    </>
  );
}

function mapDataForTable(data: MachineConfigX[]) {
  return data.map((config) => ({
    id: config?.id,
    name: config?.name,
    fuelUsed: config?.fuelUsed,
  }));
}

const Styled = {
  RefineSearchContainer: styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: ${(props) => props.theme.spacing.spacingM};
    padding-bottom: ${(props) => props.theme.spacing.spacingM};
    border-bottom: 1px solid ${(props) => props.theme.colors.black20};
  `,
  Table: styled.table`
      width: 100%;
      border-spacing: 0;
      border: 1px solid ${(props) => props.theme.colors.black30};

      tr {
          :nth-child(even) {
              background: ${(props) => props.theme.colors.seaBlueLighten};
          }
          :nth-child(odd) {
            background: ${(props) => props.theme.colors.white};
          } 

          &:hover {
            cursor: pointer;
            background-color: ${(props) => props.theme.colors.seaBlueMuted};
          }
      }

      th,
      td {
          margin: 0;
          padding: ${(props) => props.theme.spacing.spacingM};
          border-bottom: 1px solid ${(props) => props.theme.colors.black30};
          border-right: 1px solid ${(props) => props.theme.colors.black30};
          text-align: left;

          font-size: 15px;
          color: ${(props) => props.theme.colors.black30}

          :last-child {
              border-right: 0;
          }
      }
  `,
  Divider: styled.div`
    width: 700px;
    height: 4px;
    border-radius: 20px;
    background-color: ${(props) => props.theme.colors.black20};
    margin-top: ${(props) => props.theme.spacing.spacingM};
    margin-bottom: ${(props) => props.theme.spacing.spacingXL};
    align-self: center;
  `,

  Title: styled.h2`
    font-size: 20px;
    font-weight: bold;
    margin: 0;
    padding: 0;
    min-width: 180px;
  `,
  ResultsPerPageContainer: styled.div`
    display: inline-flex;
    align-items: center;
    justify-content: center;
    margin-left: ${(props) => props.theme.spacing.spacingXXS};
    min-width: 185px;
  `,
  ResultsPerPageText: styled.span`
    font-size: 15px;
    line-height: 1.27;
    color: ${(props) => props.theme.colors.black90};
    margin-right: ${(props) => props.theme.spacing.spacingXXS3};
  `,
  RightContainer: styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin-left: ${(props) => props.theme.spacing.spacingXXS};
    min-width: 180px;
  `,
};
