import { useDispatch, useSelector } from 'react-redux';
import {
  getAlertedMachines,
  getMachineConfig,
  getMachines,
} from 'store/machine/machine.selectors';
import Table from 'components/Table';
import { useEffect, useState } from 'react';
import Modal from './Modal';
import styled from 'styled-components';
import { MachineConfig } from 'store/machine/machine.types';
import {
  updateMachineConfig,
  updateAlertedMachines,
} from 'store/machine/machine.actions';

const Tile = () => {
  const [showAlert, setShowAlert] = useState<boolean>(false);
  const [pageNumber, setPageNumber] = useState<number>(1);

  const alertedMachines = useSelector(getAlertedMachines);
  const machineConfig = useSelector(getMachineConfig);
  const machines = useSelector(getMachines);

  const dispatch = useDispatch();

  useEffect(() => {
    // debugger;
    if (machineConfig && !showAlert && !alertedMachines.length) {
      const alertedMachine = machineConfig.filter((mc) => {
        const idleHours = mc.cumulativeIdleHours.hour;
        const operatingHours = mc.cumulativeOperatingHours.hour;
        console.log(operatingHours / idleHours);
        if (operatingHours / idleHours < 3) {
          return mc;
        }
        return false;
      });
      console.log(alertedMachine);
      if (alertedMachine.length) {
        dispatch(updateAlertedMachines(alertedMachine));
        setShowAlert(true);
      }
    }
  }, [machineConfig, showAlert, alertedMachines]);

  // console.log(showAlert, alertedMachines, machineConfig);

  useEffect(() => {
    // debugger;
    if (!showAlert && alertedMachines.length) {
      dispatch(updateAlertedMachines([]));
      // dispatch(updateMachineConfig([]));
    }
  }, [showAlert, alertedMachines]);

  return (
    <Styled.Dashboard>
      {showAlert && alertedMachines?.length && (
        <Modal
          onClose={() => {
            setShowAlert(false);
          }}
        >
          <Styled.Container>{alertedMachines.length}</Styled.Container>
        </Modal>
      )}
      <Table data={machines} setPageNumber={setPageNumber} />
    </Styled.Dashboard>
  );
};

const Styled = {
  Dashboard: styled.div`
    padding: ${(props) => props.theme.spacings.xl};
  `,
  Container: styled.div`
    background-color: ${(props) => props.theme.colors.white};
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
  `,
};

export default Tile;
