import { useSelector } from 'react-redux';
import { getMachineConfig, getMachines } from 'store/machine/machine.selectors';
import Table from 'components/Table';
import { useEffect, useState } from 'react';
import Modal from './Modal';
import styled from 'styled-components';
import { MachineConfig } from 'store/machine/machine.types';

const Tile = () => {
  const [showAlert, setShowAlert] = useState<boolean>(false);
  const [pageNumber, setPageNumber] = useState<number>(1);
  const [alertedMachines, setAlertedMachines] = useState<MachineConfig[]>([]);
  const machineConfig = useSelector(getMachineConfig);
  const machines = useSelector(getMachines);

  useEffect(() => {
    if (machineConfig && !showAlert) {
      const alertedMachines = machineConfig.filter((mc) => {
        const operatingHours = mc.cumulativeIdleHours.hour;
        const idleHours = mc.cumulativeOperatingHours.hour;
        if (operatingHours / idleHours < 3) {
          return mc;
        }
        return false;
      });
      if (alertedMachines.length) {
        setAlertedMachines(alertedMachines);
        setShowAlert(true);
      }
    }
  }, [machineConfig, showAlert]);

  console.log(showAlert, alertedMachines, machineConfig);

  useEffect(() => {
    if (!showAlert) {
      setAlertedMachines([]);
    }
  }, [showAlert]);

  return (
    <Styled.Dashboard>
      {showAlert && alertedMachines?.length && (
        <Modal
          onClose={() => {
            setShowAlert(false);
          }}
        >
          <Styled.Container></Styled.Container>
        </Modal>
      )}
      <Table data={machines} setPageNumber={setPageNumber} />
    </Styled.Dashboard>
  );
};

const Styled = {
  Dashboard: styled.div`
    padding: ${(props) => props.theme.spacings.l};
  `,
  Container: styled.div`
    background-color: ${(props) => props.theme.colors.white};
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
  `,
};

export default Tile;
