import { Action } from 'redux';
import { isType } from 'typescript-fsa';
import * as actions from './projects.actions';
import { State } from 'state/projects/projects.types';

export const INITIAL_STATE: State = {
  projects: [],
  pageNumber: 0,
  currentProject: null,
  loading: false,
  projectLoading: false,
  showDrawer: false,
  showSlideOutAnim: false,
};

export const projectsReducer = (state = INITIAL_STATE, action: Action) => {
  if (isType(action, actions.setProjects)) {
    return {
      ...state,
      projects: [...state.projects, ...action.payload],
    };
  }

  if (isType(action, actions.updatePageNumber)) {
    return {
      ...state,
      pageNumber: state.pageNumber + 1,
    };
  }

  if (isType(action, actions.setLoadingState)) {
    return {
      ...state,
      loading: action.payload,
    };
  }
  if (isType(action, actions.setCurrentProject)) {
    return {
      ...state,
      currentProject: action.payload,
    };
  }
  if (isType(action, actions.setProjectLoadingState)) {
    return {
      ...state,
      projectLoading: action.payload,
    };
  }

  if (isType(action, actions.showDrawer)) {
    return {
      ...state,
      showDrawer: action.payload,
    };
  }

  if (isType(action, actions.setShowSlideOutAnim)) {
    return {
      ...state,
      showSlideOutAnim: action.payload,
    };
  }
  return state;
};
