import { useDispatch, useSelector } from 'react-redux';
import {
  getAlertedMachines,
  getMachines,
} from 'store/machine/machine.selectors';
import Table from 'components/Table';
import { useEffect, useState } from 'react';
import Modal from '../components/Modal';
import styled from 'styled-components';
import { MachineConfig } from 'store/machine/machine.types';
import MachineBlock from './MachineBlock';

interface Props {
  alertedMachines: MachineConfig[];
}

const SideBar = (props: Props) => {
  const [showAlert, setShowAlert] = useState<boolean>(false);
  const [pageNumber, setPageNumber] = useState<number>(1);

  const alertedMachines = useSelector(getAlertedMachines);
  const machines = useSelector(getMachines);

  const dispatch = useDispatch();

  return (
    <Styled.SideBar>
      {alertedMachines.map((aM) => (
        <MachineBlock alertedMachine={aM} />
      ))}
    </Styled.SideBar>
  );
};

const Styled = {
  SideBar: styled.div`
    position: fixed;
    right: 0;
    height: 100vh;
    width: 40%;
    background-color: ${(props) => props.theme.colors.white};
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
  `,
};

export default SideBar;
