import React, { createContext, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { useDispatch, useSelector } from 'react-redux';
import { addAlertedMachines, updateMachines } from './machine.actions';
import { MachineConfig } from './machine.types';
import { ALERT } from 'constants/enum';
import { getAlertedMachines } from './machine.selectors';

const setAlert = (mc: MachineConfig) => {
  const idleHours = mc.cumulativeIdleHours.hour;
  const operatingHours = mc.cumulativeOperatingHours.hour;
  if (operatingHours / idleHours < 3) {
    mc.alert = ALERT.IDLE;
    return mc;
  }
  const fuelRemaining = mc.fuelRemaining.percent;

  if (fuelRemaining < 5) {
    mc.alert = ALERT.FUEL_LIMIT;
    return mc;
  }

  if (idleHours < 5 || operatingHours < 5) {
    mc.alert = ALERT.WEEKEND_OFF;
    return mc;
  }
  const engineStatus = mc.engineStatus.running;

  if (!engineStatus) {
    mc.alert = ALERT.ENGINE_FAILED;
    return mc;
  }
  return mc;
};

export const WebSocketProvider = ({ children }: any) => {
  const WebSocketContext = createContext<any>(null);
  let socket: any = useRef(null);
  let ws;

  const dispatch = useDispatch();
  const alertedMachines = useSelector(getAlertedMachines);

  useEffect(() => {
    if (!socket.current) {
      //@ts-ignore
      // eslint-disable-next-line react-hooks/exhaustive-deps
      socket.current = io('http://localhost:5000');
      console.log('++', alertedMachines);

      if (!alertedMachines.length) {
        socket.current.on('machineUpdater', (data: MachineConfig[]) => {
          console.log('++');

          dispatch(addAlertedMachines([setAlert(data[0])]));
          dispatch(updateMachines(data));
        });
      }

      // eslint-disable-next-line react-hooks/exhaustive-deps
      ws = {
        socket: socket,
      };
    }
    return () => socket.current.disconnect();
  }, [socket.current, alertedMachines]);

  return (
    <WebSocketContext.Provider value={ws}>{children}</WebSocketContext.Provider>
  );
};
