import React, { createContext } from 'react';
import io from 'socket.io-client';
import { useDispatch } from 'react-redux';
import { updatePageNumber } from './projects.actions';

const WebSocketContext = createContext<any>(undefined);

export { WebSocketContext };

// eslint-disable-next-line import/no-anonymous-default-export
export default ({ children }: any) => {
  let socket: any = null;
  let ws;

  const dispatch = useDispatch();

  const sendMessage = () => {
    const payload = new Date().toLocaleString();

    socket.emit('event://send-message', payload);
    dispatch(updatePageNumber(payload));
  };
  console.log(socket, 'socket');
  if (!socket) {
    //@ts-ignore
    socket = io.connect('http://localhost:5000');
    const payload = new Date().toLocaleString();

    socket.on('timer', (msg: string) => {
      console.log(msg, 'message----');
      dispatch(updatePageNumber(msg));
    });

    ws = {
      socket: socket,
      sendMessage,
    };
  }

  return (
    <WebSocketContext.Provider value={ws}>{children}</WebSocketContext.Provider>
  );
};
