import { useSelector, useStore } from 'react-redux';
import { getMachineConfig } from 'store/machine/machine.selectors';
import Table from 'components/Table';
import { useEffect, useState } from 'react';
import Modal from './Modal';
import styled from 'styled-components';

const Tile = () => {
  const [showAlert, setShowAlert] = useState<boolean>(false);
  const [pageNumber, setPageNumber] = useState<number>(1);
  const machineConfig = useSelector(getMachineConfig);

  useEffect(() => {
    const operatingHours = machineConfig.cumulativeIdleHours.hour;
    const idleHours = machineConfig.cumulativeOperatingHours.hour;
    if (operatingHours / idleHours < 3) {
      setShowAlert(true);
    }
  }, [machineConfig]);

  return (
    <>
      {showAlert && (
        <Modal>
          <Styled.Container>Add languages</Styled.Container>
        </Modal>
      )}
      <Table data={machineConfig} setPageNumber={setPageNumber} />
    </>
  );
};

const Styled = {
  Container: styled.div`
    background-color: ${(props) => props.theme.colors.white};
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
  `,
};

export default Tile;
