import { createSelector } from 'reselect';
import { State } from './projects.types';

export const projectsState = (state: { projectsApp: State }) => {
  return state && state.projectsApp;
};

export const getPageNumber = createSelector(
  projectsState,
  (projects) => projects.pageNumber
);
